    font-family: 'Open Sans', sans-serif;
}

.contenedor{
    width: 90%;
    max-width: 1200px;
    overflow: hidden;
    margin: auto;
    padding: 60px 0;
}

.encabezado-presentacion{
    height: 100vh;
    background-image: linear-gradient(to top, rgba(195, 194, 251, 0.486) 0%, hsla(218, 80%, 25%, 0.514) 100%), url( /309dffa4057c7536c3280a79cc52ff8f.jpg);
    background-repeat: no-repeat;
    background-position: center;
    background-size: cover;
    
}

.index{
    padding: 0;
    height: 100%;
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
    color: #fff;
}


.pestana{
    position: fixed;
    top: 30px;
    right: 30px;
    background: rgb(255, 255, 255);
    width: 40px;
    height: 40px;
    cursor: pointer;
    border-radius: 10px;
    box-shadow: 0 0 6px rgba(0, 0, 0, .5);
}

.navegacion-paginas-web{
    position: fixed;
    top: 0;
    width: 25vw;
    height: 100%;
    background-image: linear-gradient(to top, #130e55 0%, #c6c2fb 100%);
    background-image: linear-gradient(135deg, #030614 0%, #210c53 100%);
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
    right: 0;
    transform: translateX(110%);
    transition:transform 0.3s ease-in-out;
    box-shadow: 0 0 6px rgba(0, 0, 0, .5);
}

.aument{
    transform: translateX(0);
}

.titulo-web{
    font-size: 60px;
    margin-bottom: 15px;
}

