    display: flex;
    justify-content: space-evenly;
    align-items: center;
    flex-wrap: wrap;
}

.contenedor-servicio img{
    width: 25%;
}

.checklist-servicio{
    width: 40%;
}

.text-presentacion{
    margin-bottom: 25px;
}

.text-2-presentacion{
    margin-bottom: 7px;
    color: #9f8ad0;
}

.numero-presentacion{
    display: inline-block;
    background-image: linear-gradient(to top, #090514 0%, #fbc2eb 100%);
    line-height: 30px;
    text-align: center;
    border-radius: 50%;
    margin-right: 5px;
    width: 30px;
    height: 30px;
    color: #fff;
    font-weight: 700;
}


.detalles{
    width: 100%;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-evenly;
    align-items: center;
}

.cont-detalle{
    width: 30%;
    text-align: center;
    margin-bottom: 20px;
}

.cont-detalle img{
    display: block;
    margin: auto;
    width: 60%;
}

.n-expert{
    width: 100%;
    display: inline-block;
    margin-top: 20px;
    font-weight: 400;
}

footer{
    background: #1e045c;
    padding-bottom: 0.1px;
}

.footer-content{
    display: flex;
    justify-content: space-between;
    align-items: center;
